import net.minecraft.core.HolderLookup;
import net.minecraft.nbt.CompoundTag;
import net.minecraft.world.level.block.entity.BlockEntity;
import net.minecraft.world.level.block.entity.BlockEntityType;
import net.minecraft.world.level.block.state.BlockState;

/**
 * BlockEntity da Infusora de Mana - L√≥gica de processamento da m√°quina
 * 
 * Esta classe implementa toda a l√≥gica interna da Infusora de Mana,
 * incluindo armazenamento de energia, processamento de itens e gera√ß√£o de mana.
 * 
 * Sistemas integrados:
 * - Armazenamento de Forge Energy para energia de entrada
 * - Invent√°rio para catalisadores (entrada) e produtos (sa√≠da)  
 * - Sistema de receitas para convers√£o de energia em mana
 * - Sincroniza√ß√£o client-server para atualiza√ß√µes de GUI
 * - Persist√™ncia de dados via NBT
 * 
 * Esta BlockEntity serve como exemplo de como integrar sistemas tecnol√≥gicos
 * e m√°gicos de forma que ambos os tipos de jogador possam se beneficiar.
 */
public class ManaInfuserBlockEntity extends BlockEntity {

    // Constantes de configura√ß√£o da m√°quina
    private static final int MAX_ENERGY = 10000; // Capacidade m√°xima de energia
    private static final int ENERGY_PER_OPERATION = 100; // Energia consumida por opera√ß√£o
    private static final int PROCESSING_TIME = 100; // Ticks para completar uma opera√ß√£o

    // Estado interno da m√°quina
    private int energyStored = 0; // Energia atualmente armazenada
    private int processProgress = 0; // Progresso da opera√ß√£o atual (0-100)
    private boolean isProcessing = false; // Se a m√°quina est√° processando

    // TODO: Adicionar quando o sistema de invent√°rio for implementado
    // private final ItemStackHandler inventory = new ItemStackHandler(3) {
    //     @Override
    //     protected void onContentsChanged(int slot) {
    //         setChanged(); // Marca a BlockEntity como modificada
    //         sync(); // Sincroniza com o cliente
    //     }
    // };

    /**
     * Construtor da BlockEntity
     * 
     * @param type Tipo da BlockEntity (registrado no ModBlockEntities)
     * @param pos Posi√ß√£o no mundo
     * @param state Estado do bloco
     */
    public ManaInfuserBlockEntity(BlockEntityType<?> type, BlockPos pos, BlockState state) {
        super(type, pos, state);
    }

    /**
     * Construtor simplificado usando o tipo registrado
     * 
     * @param pos Posi√ß√£o no mundo
     * @param state Estado do bloco
     */
    public ManaInfuserBlockEntity(BlockPos pos, BlockState state) {
        this(ModBlockEntities.MANA_INFUSER.get(), pos, state);
    }

    /**
     * M√©todo principal de processamento - executado a cada tick
     * 
     * Este m√©todo gerencia toda a l√≥gica da m√°quina:
     * - Verifica se h√° energia suficiente
     * - Processa catalisadores se houver
     * - Atualiza progresso e estado
     * - Consome energia conforme necess√°rio
     */
    public void tick() {
        if (level == null || level.isClientSide()) {
            return; // Processa apenas no servidor
        }

        boolean wasProcessing = isProcessing;

        // Verifica se pode iniciar ou continuar processamento
        if (canProcess()) {
            if (!isProcessing) {
                startProcessing();
            }

            continueProcessing();
        } else {
            stopProcessing();
        }

        // Sincroniza com o cliente se o estado mudou
        if (wasProcessing != isProcessing) {
            setChanged();
            syncToClient();
        }

        // TODO: Implementar l√≥gica de gera√ß√£o de Mana quando o sistema for adicionado
        // if (processProgress >= PROCESSING_TIME) {
        //     generateMana();
        //     completeProcessing();
        // }
    }

    /**
     * Verifica se a m√°quina pode processar
     * 
     * @return true se pode processar, false caso contr√°rio
     */
    private boolean canProcess() {
        // Verifica energia suficiente
        if (energyStored < ENERGY_PER_OPERATION) {
            return false;
        }

        // TODO: Verificar se h√° catalisadores no slot de entrada
        // TODO: Verificar se h√° espa√ßo no slot de sa√≠da

        return true; // Temporariamente sempre true para testes
    }

    /**
     * Inicia um novo processo de convers√£o
     */
    private void startProcessing() {
        isProcessing = true;
        processProgress = 0;

        CronicasAetherium.LOGGER.debug("Infusora de Mana iniciou processamento na posi√ß√£o {}", getBlockPos());
    }

    /**
     * Continua o processo atual
     */
    private void continueProcessing() {
        processProgress++;

        // Consome energia gradualmente durante o processo
        if (processProgress % 10 == 0) { // A cada 10 ticks
            consumeEnergy(ENERGY_PER_OPERATION / 10);
        }

        // Completa o processo quando atinge o tempo necess√°rio
        if (processProgress >= PROCESSING_TIME) {
            completeProcessing();
        }
    }

    /**
     * Para o processamento atual
     */
    private void stopProcessing() {
        if (isProcessing) {
            isProcessing = false;
            processProgress = 0;

            CronicasAetherium.LOGGER.debug("Infusora de Mana parou processamento na posi√ß√£o {}", getBlockPos());
        }
    }

    /**
     * Completa um ciclo de processamento
     */
    private void completeProcessing() {
        // TODO: Implementar gera√ß√£o de mana quando o sistema for adicionado
        CronicasAetherium.LOGGER.info("Infusora de Mana completou um ciclo na posi√ß√£o {}", getBlockPos());

        // Reseta para o pr√≥ximo ciclo
        processProgress = 0;

        // Continua processando se ainda h√° recursos
        if (!canProcess()) {
            stopProcessing();
        }
    }

    /**
     * Consome energia da m√°quina
     * 
     * @param amount Quantidade de energia a consumir
     */
    private void consumeEnergy(int amount) {
        energyStored = Math.max(0, energyStored - amount);
    }

    /**
     * Adiciona energia √† m√°quina
     * 
     * @param amount Quantidade de energia a adicionar
     * @return Quantidade efetivamente adicionada
     */
    public int addEnergy(int amount) {
        int addable = Math.min(amount, MAX_ENERGY - energyStored);
        energyStored += addable;
        setChanged();
        return addable;
    }

    /**
     * Obt√©m a energia atualmente armazenada
     * 
     * @return Energia armazenada
     */
    public int getEnergyStored() {
        return energyStored;
    }

    /**
     * Obt√©m a capacidade m√°xima de energia
     * 
     * @return Capacidade m√°xima
     */
    public int getMaxEnergy() {
        return MAX_ENERGY;
    }

    /**
     * Obt√©m o progresso atual do processamento (0-100)
     * 
     * @return Progresso em porcentagem
     */
    public int getProcessProgress() {
        if (!isProcessing) return 0;
        return (processProgress * 100) / PROCESSING_TIME;
    }

    /**
     * Verifica se a m√°quina est√° processando
     * 
     * @return true se est√° processando
     */
    public boolean isProcessing() {
        return isProcessing;
    }

    /**
     * Sincroniza dados com o cliente para atualiza√ß√£o de GUI
     */
    private void syncToClient() {
        if (level != null && !level.isClientSide()) {
            // TODO: Implementar sincroniza√ß√£o quando necess√°rio
            // level.sendBlockUpdated(getBlockPos(), getBlockState(), getBlockState(), 3);
        }
    }

    // ================================
    // PERSIST√äNCIA DE DADOS (NBT)
    // ================================

    /**
     * Salva os dados da BlockEntity para NBT
     * 
     * @param tag Tag para salvar os dados
     * @param registries Lookup para registros
     */
    @Override
    protected void saveAdditional(CompoundTag tag, HolderLookup.Provider registries) {
        super.saveAdditional(tag, registries);

        tag.putInt("energy", energyStored);
        tag.putInt("progress", processProgress);
        tag.putBoolean("processing", isProcessing);

        // TODO: Salvar invent√°rio quando implementado
        // tag.put("inventory", inventory.serializeNBT(registries));
    }

    /**
     * Carrega os dados da BlockEntity do NBT
     * 
     * @param tag Tag com os dados salvos
     * @param registries Lookup para registros
     */
    @Override
    protected void loadAdditional(CompoundTag tag, HolderLookup.Provider registries) {
        super.loadAdditional(tag, registries);

        energyStored = tag.getInt("energy");
        processProgress = tag.getInt("progress");
        isProcessing = tag.getBoolean("processing");

        // TODO: Carregar invent√°rio quando implementado
        // if (tag.contains("inventory")) {
        //     inventory.deserializeNBT(registries, tag.getCompound("inventory"));
        // }
    }
}

