/**
 * Cora√ß√£o Inst√°vel - Ativador do Portal Arcano
 * 
 * Este item √© usado para ativar estruturas de portal constru√≠das com
 * blocos de Estrutura do Portal Arcano. Quando usado na estrutura correta,
 * cria um portal funcional para a dimens√£o Crisol Arcano.
 * 
 * Funcionalidades:
 * - Ativa√ß√£o de portais em estruturas v√°lidas
 * - Valida√ß√£o da forma do portal (3x3 b√°sico)
 * - Consumo do item ao ativar o portal
 * - Efeitos visuais e sonoros de ativa√ß√£o
 * - Integra√ß√£o com sistema de profici√™ncia arcana
 */
public class UnstableHeartItem extends Item {

    /**
     * Construtor do Cora√ß√£o Inst√°vel
     * 
     * @param properties Propriedades do item
     */
    public UnstableHeartItem(Properties properties) {
        super(properties);
    }

    /**
     * Manipula o uso do item em blocos
     * 
     * Verifica se o bloco alvo faz parte de uma estrutura de portal v√°lida
     * e ativa o portal se a estrutura estiver correta.
     * 
     * @param context Contexto do uso
     * @return Resultado da intera√ß√£o
     */
    @Override
    public InteractionResult useOn(UseOnContext context) {
        Level level = context.getLevel();
        BlockPos pos = context.getClickedPos();
        Player player = context.getPlayer();
        ItemStack heartStack = context.getItemInHand();

        if (level.isClientSide() || player == null) {
            return InteractionResult.SUCCESS;
        }

        BlockState clickedBlock = level.getBlockState(pos);

        // Verifica se foi clicado em um bloco de estrutura de portal
        if (clickedBlock.is(ModBlocks.ARCANE_PORTAL_FRAME.get())) {
            return attemptPortalActivation(level, pos, player, heartStack);
        }

        // Bloco inv√°lido
        player.sendSystemMessage(net.minecraft.network.chat.Component.literal(
            "¬ßcO Cora√ß√£o Inst√°vel deve ser usado em uma Estrutura do Portal Arcano."));

        return InteractionResult.FAIL;
    }

    /**
     * Tenta ativar um portal na posi√ß√£o especificada
     * 
     * @param level N√≠vel/mundo
     * @param centerPos Posi√ß√£o central do portal
     * @param player Jogador ativando
     * @param heartStack Stack do cora√ß√£o inst√°vel
     * @return Resultado da tentativa
     */
    private InteractionResult attemptPortalActivation(Level level, BlockPos centerPos, Player player, ItemStack heartStack) {
        // Verifica se h√° uma estrutura de portal v√°lida ao redor
        if (!isValidPortalStructure(level, centerPos)) {
            player.sendSystemMessage(net.minecraft.network.chat.Component.literal(
                "¬ßcEstrutura de portal inv√°lida! Construa um quadrado 3x3 de Estruturas do Portal Arcano."));
            return InteractionResult.FAIL;
        }

        // Encontra o centro da estrutura 3x3
        BlockPos portalCenter = findPortalCenter(level, centerPos);
        if (portalCenter == null) {
            player.sendSystemMessage(net.minecraft.network.chat.Component.literal(
                "¬ßcN√£o foi poss√≠vel determinar o centro do portal."));
            return InteractionResult.FAIL;
        }

        // Ativa o portal
        activatePortal(level, portalCenter, player);

        // Consome o cora√ß√£o inst√°vel
        heartStack.shrink(1);

        player.sendSystemMessage(net.minecraft.network.chat.Component.literal(
            "¬ß5‚ú® Portal Arcano ativado! O caminho para o Crisol Arcano est√° aberto."));

        return InteractionResult.SUCCESS;
    }

    /**
     * Verifica se h√° uma estrutura de portal v√°lida ao redor da posi√ß√£o
     * 
     * @param level N√≠vel/mundo
     * @param pos Posi√ß√£o a verificar
     * @return true se a estrutura √© v√°lida
     */
    private boolean isValidPortalStructure(Level level, BlockPos pos) {
        // Verifica um padr√£o 3x3 de blocos de estrutura de portal
        // com o centro vazio (onde ser√° colocado o portal ativo)

        int frameCount = 0;
        BlockPos centerCandidate = null;

        // Procura por uma estrutura 3x3 v√°lida na √°rea
        for (int dx = -1; dx <= 1; dx++) {
            for (int dz = -1; dz <= 1; dz++) {
                BlockPos checkPos = pos.offset(dx, 0, dz);
                BlockState state = level.getBlockState(checkPos);

                if (dx == 0 && dz == 0) {
                    // Centro - deve estar vazio
                    if (state.isAir()) {
                        centerCandidate = checkPos;
                    }
                } else {
                    // Bordas - devem ser estruturas de portal
                    if (state.is(ModBlocks.ARCANE_PORTAL_FRAME.get())) {
                        frameCount++;
                    }
                }
            }
        }

        // Estrutura v√°lida: 8 blocos de frame e centro vazio
        return frameCount >= 8 && centerCandidate != null;
    }

    /**
     * Encontra o centro de uma estrutura de portal v√°lida
     * 
     * @param level N√≠vel/mundo
     * @param startPos Posi√ß√£o inicial de busca
     * @return Posi√ß√£o do centro ou null se n√£o encontrar
     */
    private BlockPos findPortalCenter(Level level, BlockPos startPos) {
        // Verifica as posi√ß√µes adjacentes para encontrar o centro vazio
        for (int dx = -1; dx <= 1; dx++) {
            for (int dz = -1; dz <= 1; dz++) {
                BlockPos testCenter = startPos.offset(dx, 0, dz);
                if (level.getBlockState(testCenter).isAir()) {
                    // Verifica se esta posi√ß√£o realmente est√° no centro de uma estrutura v√°lida
                    if (isValidPortalStructure(level, testCenter)) {
                        return testCenter;
                    }
                }
            }
        }
        return null;
    }

    /**
     * Ativa o portal na posi√ß√£o especificada
     * 
     * @param level N√≠vel/mundo
     * @param centerPos Posi√ß√£o central do portal
     * @param player Jogador que ativou
     */
    private void activatePortal(Level level, BlockPos centerPos, Player player) {
        // Coloca o bloco de portal ativo no centro
        level.setBlock(centerPos, ModBlocks.ARCANE_PORTAL.get().defaultBlockState(), 3);

        // Efeitos visuais e sonoros
        level.playSound(null, centerPos, SoundEvents.END_PORTAL_TRIGGER, 
            SoundSource.BLOCKS, 1.0f, 1.0f);

        level.playSound(null, centerPos, SoundEvents.ENCHANTMENT_TABLE_USE, 
            SoundSource.BLOCKS, 0.8f, 1.2f);

        // TODO: Adicionar part√≠culas especiais quando o sistema for expandido
        // TODO: Adicionar XP de profici√™ncia arcana para o jogador
        // ModCapabilities.ifPlayerProficiencyPresent(player, proficiency -> {
        //     proficiency.addArcanaXp(ModConfig.BASE_ARCANA_XP.get() * 2); // B√¥nus por ativar portal
        // });
    }
}

